export const initialState={
    isLoggingIn: false,//로그인시도중
    isLoggingOut: false,
    isLoggedIn:false,//로그아웃시도중
    me:null,
    signUpData:{},
    loginData:{},
}
export const loginRequestAction = (data)=>{
    return{
        type:'LOG_IN_REQUEST',
        data,
    }
}

export const logoutRequestAction = ()=>{
    return{
        type:'LOG_OUT_REQUEST',
    }
}



//(이전상태,액션) => 다음상태
const reducer = (state = initialState,action)=>{
    switch (action.type){
        case 'LOG_IN_REQUEST':
            return{
                    ...state,
                    isLoggingIn: true,
                };
        case 'LOG_IN_SUCCESS':
            return{
                ...state,
                isLoggingIn: false,
                isLoggedIn: true,
                me:{...action.data,nickname:'theon2'},
            };
        case 'LOG_IN_FAILURE':
            return{
                ...state,
                isLoggedIn: false,
                isLoggingIn: false,
            };

        case 'LOG_OUT_REQUEST':
            return{
                ...state,
                isLoggingOut: true,
            };
        case 'LOG_OUT_SUCCESS':
            return{
                ...state,
                isLoggedIn:false,
                isLoggingOut: false,
                me:null,
            };
        case 'LOG_OUT_FAILURE':
            return{
                ...state,
                isLoggingOut: false,
            };
        default:
            return state;
    }
};

export default reducer;
