import {HYDRATE} from "next-redux-wrapper";
import {combineReducers} from "redux";
import user from "./user";
import post from "./post";

const initialState={
    user:{

    },
    post:{

    }
}

export const loginAction = (data)=>{
    return{
        type:'LOG_IN',
        data,
    }
}

export const logoutAction = ()=>{
    return{
        type:'LOG_OUT',
    }
}

//(이전상태,액션) => 다음상태
const rootReducer = combineReducers({
    index: (state = {},action)=>{
    switch (action.type) {
        case HYDRATE:
            console.log('HYDRATE', action);
            return {
                ...state, ...action.payload
            };
        case 'LOG_IN':
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: true,
                    user: action.data
                }
            }
        case 'LOG_OUT':
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: false,
                    user: null
                }
            };
        default:
            return state;
    }
    },
        user,
            post,
});

export default rootReducer;
