import { useRouter } from 'next/router';
import React from 'react';
import axios from 'axios';
import { END } from 'redux-saga';
import { useSelector } from 'react-redux';
import { Head } from 'next/document';
import wrapper from '../../store/configureStore';
import { LOAD_MY_INFO_REQUEST } from '../../reducers/user';
import { LOAD_SPOST_REQUEST } from '../../reducers/post';
import AppLayout from '../../components/AppLayout';
import PostCard from '../../components/PostCard';

function Post() {
  const router = useRouter();
  const { id } = router.query;
  const { singlePost } = useSelector((state) => state.post);
  const { userInfo } = useSelector((state) => state.user);

  return (
    <AppLayout>
      <Head>
        <title>
          {singlePost.User.nickname}
          님의 글
        </title>
        <meta name="description" content={`${singlePost.User.nickname}님의 게시글`} />
        {/*<meta property="og:title" content={`${userInfo.nickname}님의 게시글`} />*/}
        {/*<meta property="og:description" content={`${userInfo.nickname}님의 게시글`} />*/}
        <meta property="og:image" content="https://nodebird.com/favicon.png" />
        {/*<meta property="og:url" content={`https://nodebird.com/user/${id}`} />*/}
      </Head>
      <PostCard post={singlePost} />

    </AppLayout>
  );
}

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {
  const cookie = context.req ? context.req.headers.cookie : '';
  axios.defaults.headers.Cookie = cookie;
  axios.defaults.headers.Cookie = '';
  if (context.req && cookie) { // 타 유저간 쿠키가 공유되는 문제를 방지하기 위함
    axios.defaults.headers.Cookie = cookie;
  }

  context.store.dispatch({
    type: LOAD_MY_INFO_REQUEST,
  });
  context.store.dispatch({
    type: LOAD_SPOST_REQUEST,
    data: context.params.id,
  });
  context.store.dispatch(END);
  await context.store.sagaTask.toPromise();
});

export default Post;
