import {HYDRATE} from "next-redux-wrapper";

export const initialState={
    mainPosts:[],
}


//(이전상태,액션) => 다음상태
const reducer = (state = initialState,action)=>{
    switch (action.type){
        case HYDRATE:
            console.log('HYDRATE',action);
            return {
                ...state,...action.payload
            };

        default:
            return state;
    }
};

export default reducer;
