import React from "react";
import {Button, Card} from "antd";
import {HeartOutlined, RetweetOutlined} from "@ant-design/icons";

const PostCard=({post})=>{
    return (
        <div>
            <Card
                cover={post.Image[0] && <PostImages images={post.Images}/>}
                actions={[
                    <RetweetOutlined/>,
                    <HeartOutlined/>
                ]}
            >
                <Image/>
                <Content/>
                <Button></Button>
            </Card>
            <CommentForm/>
            <Comments/>
        </div>
    );
}

export default PostCard;
