import AppLayout from "./../components/AppLayout";
import React, {useCallback, useState} from "react";
import Head from "next/head";
import {Button, Checkbox, Form, Input} from "antd";
import useInput from "../hooks/useInput";
import styled from "styled-components";

const ErrorMessage = styled.div`
color:red`;

const Signup = () => {
    const [id,onChangeId] = useInput('');
    const [nickname,onChangeNickname] = useInput('');
    const [password,onChangePassword] = useInput('');

    const [password2, setPassword2] = useState('');
    const [password2Error, setPassword2Error] = useState(false);
    const onChangePassword2 = useCallback((e)=>{
        setPassword2(e.target.value);
        setPassword2Error(e.target.value !== password);
    },[]);

    const [term, setTerm] = useState(false);
    const [termError, setTermError] = useState(false);
    const onChangeTerm = useCallback((e)=>{
        setTerm(e.target.checked);
        setTermError(false);
    },[]);

    const onSubmit = useCallback(() => {
        if(password !== password2)return setPassword2Error(true);
        if(!term)return setTermError(true);
        console.log(id,nickname,password);
        }, [password,password2,term]);

    return (
        <AppLayout>
            <Head>
                <title>회원가입 | NodeBird</title>
            </Head>
            <Form onFinish={onSubmit}>
                <div>
                    <label htmlFor="user-id">아이디</label>
                    <br />
                    <Input name="user-id" value={id} onChange={onChangeId} requied />
                </div>
                <div>
                    <label htmlFor="user-id">닉네임</label>
                    <br />
                    <Input name="user-id" value={nickname} onChange={onChangeNickname} requied />
                </div>
                <div>
                    <label htmlFor="user-password">비밀번호</label>
                    <br />
                    <Input
                        name="user-password"
                        type="password"
                        value={password}
                        onChange={onChangePassword}
                        requied
                    />
                </div>
                <div>
                    <label htmlFor="user-password">비밀번호</label>
                    <br />
                    <Input
                        name="user-password2"
                        type="password"
                        value={password2}
                        onChange={onChangePassword2}
                        requied
                    />
                    {password2Error && <ErrorMessage>비밀번호가 일치하지 않습니다.</ErrorMessage>}
                </div>
                <div>
                    <Checkbox name="user-term" checked={term} onChange={onChangeTerm}>김도원 말을 잘 들을 것에 동의힙니다.</Checkbox>
                    {termError && <ErrorMessage>약관에 동의 하셔야 합니다.</ErrorMessage>}
                </div>
                <div style={{marginTop:10}}>
                    <Button type="primary" htmlType="submit">가입하기</Button>
                </div>
            </Form>

        </AppLayout>
    );
};

export default Signup;
