import React from "react";
import {Button, Card} from "antd";

const PostCard=({post})=>{
    return (
        <div>
            <Card
                cover={post.Image[0] && <PostImage images={post.Images}/>}
            >
                <Image/>
                <Content/>
                <Button></Button>
            </Card>
            <CommentForm/>
            <Comments/>
        </div>
    );
}

export default PostCard;
